-- ----------------------------
-- Table structure for knowledge_document
-- ----------------------------
DROP TABLE IF EXISTS `knowledge_document`;
CREATE TABLE `knowledge_document`  (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '文档ID',
  `title` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '文档标题',
  `docType` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '文档类型（ich_intro/ich_history/merchant_info等）',
  `fileName` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '原始文件名',
  `filePath` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '文件存储路径',
  `fileSize` bigint NULL DEFAULT NULL COMMENT '文件大小（字节）',
  `projectId` bigint NULL DEFAULT NULL COMMENT '关联的非遗项目ID',
  `merchantId` bigint NULL DEFAULT NULL COMMENT '关联的商户ID',
  `tags` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '标签',
  `chunkCount` int NULL DEFAULT 0 COMMENT '切片数量',
  `vectorStatus` tinyint NULL DEFAULT 0 COMMENT '向量化状态（0-待处理 1-处理中 2-已完成 3-失败）',
  `createUserId` bigint NULL DEFAULT NULL COMMENT '创建用户ID',
  `createdAt` datetime NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updatedAt` datetime NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  `isDelete` tinyint NULL DEFAULT 0 COMMENT '是否删除',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `idx_docType`(`docType` ASC) USING BTREE,
  INDEX `idx_projectId`(`projectId` ASC) USING BTREE,
  INDEX `idx_merchantId`(`merchantId` ASC) USING BTREE,
  INDEX `idx_vectorStatus`(`vectorStatus` ASC) USING BTREE,
  INDEX `idx_createUserId`(`createUserId` ASC) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 20 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = '知识库文档表' ROW_FORMAT = Dynamic;

